import React, { useEffect } from 'react'
import { connect } from 'react-redux'
import { addTodo, toDo } from './actions'

import { useDispatch, useSelector } from 'react-redux'

const AddTodo = ({ state }) => {
    let input
    let list = [];
    const dispatch = useDispatch();

    // const content = useSelector(state => state.toDo);

    useEffect(() => {
        // list = dispatch(toDo)
        console.log('listtttttttttttttt', state)
    }, [])

    let data = [];
    useEffect(() => {
        data = JSON.parse(localStorage.getItem('persistantState'));
        console.log(data, '..........................')
    }, [])


    return (
        <>
            <div>
                <form onSubmit={e => {
                    e.preventDefault()
                    if (!input.value.trim()) {
                        return
                    }
                    dispatch(addTodo(input.value))
                    input.value = ''
                }}>
                    <input ref={node => input = node} />
                    <button type="submit">
                        Add Todo
                    </button>
                </form>
                <button onClick={() => dispatch({ type: 'TODO' })} type="button">+</button>



            </div>

            <div>
                <h4>data</h4>
                {data ? data.map((data) => <h2>{data.text}</h2>) : 'no record'}

            </div>
        </>
    )
}

export default connect()(AddTodo)






