export const saveState = (state) => {
    // const data = JSON.parse(localStorage.getItem("appState")) || [];
    try {
        const data = JSON.parse(localStorage.getItem("appState")) || [];
      const serialState = JSON.stringify(state);
      const allData = data.push(serialState)
      localStorage.setItem('appState', allData);
    } catch(err) {
        console.log(err);
    }
};



