import React, { useEffect, useState } from 'react'
import { connect } from 'react-redux'
import { addTodo, deleteTodo, toDo,completeTodo } from './actions'
import { useDispatch, useSelector } from 'react-redux'

const AddTodo = ({ todos }) => {
  const dispatch = useDispatch()

  console.log(todos, 'todos')

  // const record = useSelector(state => state);
  // let input

  const [input, setInput] = useState('');

  const updateInput = input => {
    setInput(input)
  }

  const handleAddTodo = () => {
    dispatch(addTodo(input));
    setInput(input)
  }


  useEffect(() => {
    let data = JSON.parse(localStorage.getItem('persistantState'));
    // console.log(data,'..........................')
  }, [])


  return (
    <>
      <div>

        <input onChange={(e) => updateInput(e.target.value)} value={input} />
        <button onClick={handleAddTodo}>add</button>

      </div>

      <div>
        <h4>data</h4>
        {todos ? todos.id.map((item, index) => {
          return (
            <>
              <li>{item.text}
              <button onClick={()=>dispatch(completeTodo(item.id))}>d</button>
              {/* <button onClick={() => dispatch(completedTodo(item.id))}>
              {todos && todos.completed ? "ðŸ‘Œ" : "ðŸ‘‹"}{" "}
              </button> */}
                <button key={item.id} onClick={() => dispatch(deleteTodo(item.id))}>delete</button>
                {/* <button onClick={() => handleRemoveCourse()}>delete</button> */}
              </li>

            </>
          )
        }
        ) : 'no record'}

      </div>
    </>
  )
}

const mapStateToProps = (item) => {
  const todos = toDo(item);
  return { todos };
}

export default connect(mapStateToProps, { addTodo })(AddTodo)






