const reducer = (state = [], action) => {
    console.log(action,'---------------',state)
    switch (action.type) {
      case 'ADD_TODO':
        return [
          ...state,
          {
            id: action.id,
            text: action.text
          },
          localStorage.setItem('data',JSON.stringify(action))
        ]     

        case 'TODO':
        return state.map(todo =>
            console.log(todo,'todo')
          (todo.id === action.id)
            ? {...todo, completed: !todo.completed}
            : todo
        )
      default:
        return state
    }
  }

  export default reducer


