import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
// import { deleteUser } from './actions';

class Row extends React.Component {

    constructor(props)
    {
        super(props);

        // this.handleDelete = this.handleDelete.bind(this);
    }

    // handleDelete(e) {
    //     e.preventDefault();

    //     if(confirm("Are you sure?")) {
    //         this.props.deleteUser(this.props.user.id);
    //     }
    // }

    render()
    {
        return (
            <tr>
                <td>{this.props.user.id}</td>
                <td>{this.props.user.name}</td>
                <td> {this.props.user.email}</td>
                <td>{this.props.user.is_admin==1?'Yes':'No'}</td>
                {/* <td>{this.props.user.role==2?<p style={{color:'#ff0080'}}>Transportal</p>:'Customer'}</td>
                <td>{this.props.user.status==1?<p className="active">Active</p>:<p className="deactive">Deactive</p>}</td> */}
                <td>{this.props.user.created_at}</td>


                <td>
                    <Link to={'/users/edit/' + this.props.user.id} className="btn btn-sm bg-success-light mr-2">
                    <i className="far fa-edit mr-1"></i></Link>
                    {/* <a href="#" className="btn btn-danger btn-sm" onClick={this.handleDelete}><i
                        className="far fa-trash-alt mr-1"></i></a> */}

                </td>
            </tr>
        )
    }
};

const mapDispatchToProps = (dispatch) => {
    return {
        deleteUser: (id) => dispatch(deleteUser(id))
    }
};

export default connect(null, mapDispatchToProps)(Row);
