import React, { useEffect, useState } from 'react'
import { connect } from 'react-redux'
import { addBlog, deleteTodo, toBlog, toDo } from './actions'
import { useDispatch, useSelector } from 'react-redux'

const Blog = ({ blog }) => {
  const dispatch = useDispatch()

  console.log(blog, 'blog')

  // const record = useSelector(state => state);
  // let input

  const [inputBlog, setInputBlog] = useState('');

  const updateInput = input => {
    setInputBlog(input)
  }

  const handleAddBlog = () => {
    dispatch(addBlog(inputBlog));
    setInputBlog(inputBlog)
    console.log('blog',inputBlog)
  }


//   useEffect(() => {
//     let data = JSON.parse(localStorage.getItem('persistantState'));
//     // console.log(data,'..........................')
//   }, [])


  return (
    <>
      <div>

        <input placeholder="add blog" onChange={(e) => updateInput(e.target.value)} value={inputBlog} />
        <button onClick={handleAddBlog}>Add</button>

      </div>

      <div>
        <h4>data</h4>
        {/* {todos ? todos.id.reducer.map((item, index) => {
