    console.log(data,'..........................')
  }, [])


  return (
    <>
      <div>

        <input onChange={(e)=>updateInput(e.target.value)} value={input}/>
        {/* <form onSubmit={e => {
          e.preventDefault()
          if (!input.value.trim()) {
            return
