const reducer = (state = [], action) => {
    console.log(action, '---------------', state)
    switch (action.type) {
        case 'ADD_TODO':
            return [
                ...state,
                {
                    id: action.id,
                    text: action.text
                }
            ]

        case 'TODO':
            let data = JSON.parse(localStorage.getItem('persistantState'))
            console.log(data, 'dataaaaaaaaaa');
            return data

        case 'REMOVE_TODO':
            //    return{
            //     id: action.id,
            //     ...state.slice(action.id, 1)
            //    }

            let updatedList = state.filter(({ id }) => id !== action.id);
            let updateLocal = localStorage.setItem('persistantState',updatedList);
            return updateLocal

            // return state.filter(({ id }) => id !== action.id);

        default:
            return state
    }
}

export default reducer


