const reducer = (state = [], action) => {
    console.log(state,'state',action)
    switch (action.type) {
      case 'ADD_TODO':
        return [
          ...state,
          {
            id: action.id,
            text: action.text
          },
          localStorage.setItem('data',JSON.stringify(action))
        ]     
    }
  }

  export default reducer



