    console.log(action,'---------------',state)
    switch (action.type) {
      case 'ADD_TODO':
        return [
          ...state,
          {
            id: action.id,
            text: action.text
          }
        ]     

        case 'TODO':
            let data = JSON.parse(localStorage.getItem('persistantState'))
            console.log(data,'dataaaaaaaaaa');
            return data

      default:
        return state
        localStorage.setItem('data',JSON.stringify(state))
    }
  }

  export default reducer


