const reducer = (state = [], action) => {
    // console.log(action, '---------------', state)
    switch (action.type) {
        case 'ADD_TODO':
            return [
                ...state,
                {
                    id: action.id,
                    text: action.text
                }
            ]

        case 'TODO':
            let data = JSON.parse(localStorage.getItem('persistantState'))
            // console.log(data, 'datataaaaaaaaaaaaa=============aaa')
            return data;


        case 'FETCH_PRODUCTS_SUCCESS':
            let list = JSON.parse(localStorage.getItem('persistantState'))
            return {
                ...state,
                state: action.payload.list
            };

        case 'TOGGLE_TODO':
            return state.map(todo =>
                (todo.id === action.id)
                    ? { ...todo, completed: !todo.completed }
                    : todo
            )

        default:
            return state
        // localStorage.setItem('data',JSON.stringify(state))
    }
}

export default reducer


